func part1(input1 []int, input2 []int) {
	sort.Ints(input1)
	sort.Ints(input2)

	result := 0
	for i := 0; i < len(input1); i++ {
		diff := input1[i] - input2[i]
		if diff < 0 {
			diff = diff * -1
		}
		result += diff
	}

	print(fmt.Sprintf("Part 1: %d\n", result))
}

func part2(input1 []int, input2 []int) {
	result := 0
	for i := 0; i < len(input1); i++ {
		compare := input1[i]
		count := count(input2, compare)
		result += compare * count
	}

	print(fmt.Sprintf("Part 2: %d\n", result))
}

func count(slice []int, target int) int {
	count := 0
	for _, num := range slice {
		if num == target {
			count++
		}
	}
	return count
}
